    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Liste employés{% endblock %}

{% block body %}

<div class=\"container\">
    
    <center><h2>Liste des employés</h2></center>
   
    <table class=\"table table-striped table-bordered\" id=\"table\" style=\"width: 100%\">
        <thead>
            <th>Matricule</th>
            <th>Nom</th>
            <th>Date de Naissance</th>
            <th>Poste</th>
            <th>Salaire</th>          
            <th></th>
        </thead>

        <tbody>
            {% for personne in employes %}   
            <tr>
                <td>{{personne.matricule}}</td>
                <td>{{personne.nomComplet}}</td>
                <td>{{personne.dateNaissance | date('d/m/Y')}}</td>
                <td>{{personne.idService.libelle}}</td>
                <td>{{personne.salaire}}</td>
                <td><a href=\"{{path('ent_edit',{'id':personne.id}) }}\"><button class=\"btn btn-outline-info\">Modifier</button></a>
                <a href=\"{{path('valid',{'id':personne.id}) }}\"><button class=\"btn btn-outline-danger\">Supprimer</button></a></td>            
            </tr>
            {% endfor %}
        </tbody>
    </table>
</div>
{% block javascripts %}
 
{% endblock %}
   
{% endblock %}

", "ent/all.html.twig", "/home/mister_d/entreprise/templates/ent/all.html.twig");
